    //   </Row>
    // </>

    <Form noValidate validated={validated} onSubmit={handleSubmit}>
      <Row>
        <Form.Group as={Col} md='4' controlId='validationCustom01'>
          <Form.Label>First name</Form.Label>
          <Form.Control
            required
            type='text'
            placeholder='First name'
            defaultValue='Mark'
          />
          <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        </Form.Group>
      </Row>
    </Form>
  )
}

function Number () {
  const [max, setMax] = useState('')
  const [isChecked, setChecked] = useState(false)

  const nums = [
    { value: 10 },
    { value: 25 },
    { value: 50 },
    { value: 100 },
    { value: 500 },
    { value: 1000 }
  ]

  const change = e => {
    setMax(parseInt(e.target.value))
    setChecked(true)
  }

  return (
    <Container>
      <h1>Pick a number:</h1>
      <Row className='justify-content-md-center'>
        <Col lg='5'>
          {nums.map((num, idx) => (
            <>
              <ToggleButton
                key={idx}
                id={`radio-${idx}`}
                className='m-1'
                type='radio'
                variant='outline-primary'
                name='radio'
                value={num.value}
                checked={max === parseInt(num.value)}
                onChange={e => change(e)}
                disabled={isChecked}
              >
                {num.value}
              </ToggleButton>
            </>
          ))}
        </Col>
      </Row>

      <GenerateRandomNum max={max} isChecked={isChecked} />
    </Container>
  )
}

const GenerateRandomNum = props => {
  const num = props.max
  var randomNum = Math.floor(Math.random() * (num - 1)) + 1
  if (randomNum % 5 == 0) {
    randomNum = Math.floor(Math.random() * (num - 1)) + 1
  }

  return (
    <>
      <LetUserPick randomNum={randomNum} isChecked={props.isChecked} />
    </>
  )
}

export default Number
