function Number () {
  const [max, setMax] = useState(0)
  const [checked, setChecked] = useState(false)
  const [radioValue, setRadioValue] = useState('1')

  const radios = [
    { name: 'Active', value: '1' },
    { name: 'Radio', value: '2' },
    { name: 'Radio', value: '3' }
  ]
  const changeValue = e => {
    setMax(parseInt(e.target.value))
    e.target.checked = true
  }
  console.log(max)

  return (
    <>
      <ButtonGroup className='mb-2'>
        <ToggleButton
          id='toggle-check'
          type='checkbox'
          variant='secondary'
          checked={checked}
          value='1'
          onChange={e => setChecked(e.currentTarget.checked)}
        >
          Checked
        </ToggleButton>
      </ButtonGroup>
      <br />
      <ButtonGroup className='mb-2'>
        {radios.map((radio, idx) => (
          <ToggleButton
            key={idx}
            id={`radio-${idx}`}
            type='radio'
            variant='secondary'
            name='radio'
            value={radio.value}
            checked={radioValue === radio.value}
            onChange={e => setRadioValue(e.currentTarget.value)}
          >
            {radio.name}
          </ToggleButton>
        ))}
      </ButtonGroup>
      <br />
      <ToggleButton
        className='mb-2'
        id='toggle-check'
        type='checkbox'
        variant='outline-primary'
        checked={checked}
        value='1'
        onChange={e => setChecked(e.currentTarget.checked)}
      >
        Checked
      </ToggleButton>
      <br />
      <ButtonGroup>
        {radios.map((radio, idx) => (
          <ToggleButton
            key={idx}
            id={`radio-${idx}`}
            type='radio'
            variant={idx % 2 ? 'outline-success' : 'outline-danger'}
            name='radio'
            value={radio.value}
            checked={radioValue === radio.value}
            onChange={e => setRadioValue(e.currentTarget.value)}
          >
            {radio.name}
          </ToggleButton>
        ))}
      </ButtonGroup>
    </>
  )
}

export default Number
