import { useState } from "react";
import data from './data.json'


import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';

import Container from 'react-bootstrap/Container';
import ListGroup from 'react-bootstrap/ListGroup';
import 'bootstrap/dist/css/bootstrap.min.css';
// import "./App.css"


function generateWords(w, notLetters) {
    // w = w.toUpperCase();
    // notLetters = notLetters.toUpperCase();
    var format = /[ `!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;
    if (format.test(w) === true) {
        const words = data.words;
        var wordsFnd = [];
        var no_letters_pattern;
        if (notLetters.length < 1) {

            no_letters_pattern = new RegExp("^[^]*$");
        } else {
            no_letters_pattern = new RegExp("^[^" + notLetters + "]*$");
        }
        var pattern = new RegExp(w, 'g');
        for (let i = 0; i < words.length; i++) {


            if (words[i].match(pattern) != null && words[i].match(pattern)[0].length == words[i].length && words[i].match(no_letters_pattern) != null) {
                const word = words[i][0].toUpperCase() + words[i].slice(1, words[i].length).toLowerCase();
                wordsFnd.push(word)
            } else {
                // alert("Couldn't find it!")
            }



        }

        return wordsFnd;
    }

    return [];

}

function Hangman() {

    const [word, setWord] = useState("")
    const [notLetters, setNotLetters] = useState("")
    const [wordLen, setWordLength] = useState(0)
    var wordsFND;
    const ChangeValue = (event) => {
        setWord(event.target.value);
        setWordLength(word.length)

    }

    const ChangeNotLetter = (event) => {
        setNotLetters(event.target.value);


    }



    return (
        <>


            <Container>
                <h1 className="pt-5 pb-5">This game is to see available choices for your hangman round(Make sure you put some letters like vowels first before checking). Replace missing letters with dots. If there are letters that are not in the word, make sure to put it in the second input box and don't seperate each letter with a comma: </h1>
                <Row className="justify-content-md-center">
                    <Col xs lg="5"><Form.Control type="text" placeholder="Word:" value={word} onChange={ChangeValue} autoFocus /></Col>

                </Row>

                <Row className="justify-content-md-center mt-3 mb-3">
                    <Col xs lg="5"><Form.Control type="text" placeholder="Letters not in word: " value={notLetters} onChange={ChangeNotLetter} /></Col>
                </Row>
                <ListGroup>
                    {
                        generateWords(word, notLetters).map((w, i) => (
                            <Row className="justify-content-md-center mt-1">
                                <Col xs lg="5"><ListGroup.Item xs lg="2" key={i} variant="primary">{w}</ListGroup.Item></Col>
                            </Row>





                        ))
                    }
                </ListGroup>
            </Container>


        </>
    )
}

export default Hangman;
