function LetUserPick (props) {
  console.log(props.num)
  return (
    <>
      <input type={'text'} value={props.randomNum} />
    </>
  )
}

function Number () {
  const [max, setMax] = useState('')
  const [isChecked, setChecked] = useState(false)

  const nums = [
    { value: 10 },
    { value: 25 },
    { value: 50 },
    { value: 100 },
    { value: 500 },
    { value: 1000 }
  ]

  const change = e => {
    setMax(parseInt(e.target.value))
    setChecked(true)
  }

  return (
    <Container>
      <h1>Pick a number:</h1>
      <Row className='justify-content-md-center'>
        <Col lg='6'>
          {nums.map((num, idx) => (
            <>
              <ToggleButton
                key={idx}
                className='m-2'
                id={`radio-${idx}`}
                type='radio'
                variant='outline-primary'
                name='radio'
                value={num.value}
                checked={max === parseInt(num.value)}
                onChange={e => change(e)}
                disabled={isChecked}
              >
                {num.value}
              </ToggleButton>
            </>
          ))}
        </Col>
      </Row>
      <Row className='justify-content-md-center'>
        <Col lg='6'>
          <GenerateRandomNum max={max} isChecked={isChecked} />
        </Col>
      </Row>
    </Container>
  )
}

const GenerateRandomNum = props => {
  const num = props.max
  var randomNum = Math.floor(Math.random() * (num - 1)) + 1
  if (randomNum % 5 == 0) {
    randomNum = Math.floor(Math.random() * (num - 1)) + 1
  }

  return (
    <>
      <LetUserPick randomNum={randomNum} />
    </>
  )
}

export default Number
