import { useState } from "react";
import data from './data.json'



function generateWords(w, notLetters) {
    // w = w.toUpperCase();
    notLetters = notLetters.toUpperCase();
    const words = data.words;
    var wordsFnd = [];
    var no_letters_pattern;
    if (notLetters.length < 1) {

        no_letters_pattern = new RegExp("^[^]*$");
    } else {
        no_letters_pattern = new RegExp("^[^" + notLetters + "]*$");
    }
    var pattern = new RegExp(w, 'g');
    // console.log('MONKEY'.match(pattern)[0]);
    console.log(words[50]);
    for (let i = 0; i < words.length; i++) {
        console.log(words[i]);


        if (words[i].match(pattern) != null && words[i].match(pattern)[0].length == words[i].length && words[i].toUpperCase().match(no_letters_pattern) != null) {
            const word = words[i][0].toUpperCase() + words[i].slice(1, words[i].length).toLowerCase();
            wordsFnd.push(word)
        } else {
            console.log("not fond!");
        }

        console.log(wordsFnd);


    }

    return wordsFnd;
}

function Hangman() {

    const [word, setWord] = useState("")
    const [notLetters, setNotLetters] = useState("")
    var wordsFND;
    const ChangeValue = (event) => {
        setWord(event.target.value);
        wordsFND = generateWords(word, notLetters)
        console.log(wordsFND);

    }
    console.log(wordsFND);

    const ChangeNotLetter = (event) => {
        setNotLetters(event.target.value);
        wordsFND = generateWords(word, notLetters)

    }

    return (
        <div className="element">
            <h1>This game is to see available choices for your hangman round. Replace missing letters with dots. If there are letters that are not in the word, make sure to put it in the second input box and don't seperate each letter with a comma: </h1>
            <input placeholder="Your word: " value={word} onChange={ChangeValue} autoFocus />
            <br /><br />
            <input placeholder="Letters that are not in the word:" value={notLetters} onChange={ChangeNotLetter} />

            <ul>
                {generateWords(word, notLetters).map((w, i) => (
                    <li key={i}>{w}</li>
                ))}
            </ul>
        </div>
    )
}

export default Hangman;
