import { useState } from "react";
import data from './data.json'
import "./App.css"
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';

import Container from 'react-bootstrap/Container';



var colorDict = {
    3: '#6D3705',
    2: '#D1F2B3',
    10: '#9955E7',
    16: '#716EA1',
    17: '#9D778E',
    18: '#EE76B9',
    7: '#8511B9',
    19: '#827F50',
    22: '#488A6F',
    24: '#FED1BB',
    5: '#220985',
    1: '#CA825A',
    14: '#F3D364',
    4: '#740F34',
    15: '#4D836D',
    6: '#1139C3',
    9: '#A2490B',
    12: '#2D953D',
    13: '#C36E52',
    20: '#DC7A54',
    21: '#698317',
    8: '#9F2B33',
    23: '#845FE0',
    11: '#1F8771'
}


function generateWords(w, notLetters) {
    // w = w.toUpperCase();
    // notLetters = notLetters.toUpperCase();
    const words = data.words;
    var wordsFnd = [];
    var no_letters_pattern;
    if (notLetters.length < 1) {

        no_letters_pattern = new RegExp("^[^]*$");
    } else {
        no_letters_pattern = new RegExp("^[^" + notLetters + "]*$");
    }
    var pattern = new RegExp(w, 'g');
    for (let i = 0; i < words.length; i++) {


        if (words[i].match(pattern) != null && words[i].match(pattern)[0].length == words[i].length && words[i].match(no_letters_pattern) != null) {
            const word = words[i][0].toUpperCase() + words[i].slice(1, words[i].length).toLowerCase();
            wordsFnd.push(word)
        } else {
            // alert("Couldn't find it!")
        }



    }

    return wordsFnd;


}

function Hangman() {

    const [word, setWord] = useState("")
    const [notLetters, setNotLetters] = useState("")
    const [wordLen, setWordLength] = useState(0)
    var wordsFND;
    const ChangeValue = (event) => {
        setWord(event.target.value);
        setWordLength(word.length)

    }

    const ChangeNotLetter = (event) => {
        setNotLetters(event.target.value);


    }



    return (
        <>
            <h1>This game is to see available choices for your hangman round. Replace missing letters with dots. If there are letters that are not in the word, make sure to put it in the second input box and don't seperate each letter with a comma: </h1>
            <Container>
                <Row>
                    <Col><Form.Control type="text" /></Col>
                    <br /><br />
                    <Col><Form.Control type="text" /></Col>
                </Row>

            </Container>
            <Form.Control placeholder="Your word: " value={word} onChange={ChangeValue} autoFocus />

            <input placeholder="Letters that are not in the word:" value={notLetters} onChange={ChangeNotLetter} />

            <ul>
                {
                    generateWords(word, notLetters).map((w, i) => (

                        <li key={i} style={{ color: colorDict.wordLen }}>{w}</li>




                    ))
                }
            </ul>
        </>
    )
}

export default Hangman;
