import { useState, useEffect } from "react";

function Hangman() {
    const [word, setWord] = useState("")

    console.log(word)
    useEffect(() => {
        return (<h1>hi,{word}</h1>)
    }, [word]);
    const changeValue = event => setWord(event.target.value)
    return (
        <div className="element">
            <h1>Contact Little Lemon on this page.</h1>
            <input placeholder="why" value={word} onChange={changeValue} />
        </div>
    )
}



export default Hangman;
