const guesses = []
function LetUserPick (props) {
  const [tries, setTries] = useState(0)
  const [validated, setValidated] = useState(false)
  const [correctGuess, setCorrectGuess] = useState(null)
  const [guess, setGuess] = useState('')
  const [textAlert, setTextAlert] = useState('')
  const [show, setShow] = useState(false)

  function store (e) {
    setGuess(e.target.value)
  }

  function check (guess) {
    if (parseInt(guess) == parseInt(props.randomNum)) {
      setCorrectGuess(true)
      setTextAlert('Correct! You got in ' + guesses.length + ' tries.')
    } else {
      setCorrectGuess(false)
      setGuess('')
    }
  }

  useEffect(() => {
    console.log(correctGuess)
    console.log(guesses)
  })
  function submit (event) {
    setGuess(event.target.value)
    guesses.push(parseInt(guess))
    event.preventDefault()

    check(guess)
    setShow(true)
  }

  return (
    <>
      <h3 style={{ visibility: props.isChecked ? 'visible' : 'hidden' }}>
        The computer has generated a random number from 1 to {props.max}. Try to
        guess it:
      </h3>
      <Row className='justify-content-md-center'>
        <Col xs lg='5'>
          <form onSubmit={submit}>
            <Form.Control
              type='text'
              className={
                'pb-4 ' + correctGuess == true
                  ? 'text-success border-success'
                  : ''
              }
              autoFocus={true}
              placeholder='Your Guess:'
              onChange={e => store(e)}
              value={guess}
              disabled={correctGuess ? true : false}
              style={{ visibility: props.isChecked ? 'visible' : 'hidden' }}
            />
            {/* This is alert to tell user if low or high:  */}
          </form>
        </Col>
      </Row>
      <ListGroup>
        {guesses.map((w, i) => (
          <Row className='justify-content-md-center mt-3' key={i.toString()}>
            <Col xs='true' lg='1' mx-auto>
              <Alert
                xs='true'
                className='text-center'
                lg='2'
                key={i.toString()}
                variant={
                  parseInt(w) == parseInt(props.randomNum)
                    ? 'success'
                    : 'danger'
                }
              >
                {w + ':'}
              </Alert>
            </Col>
            <Col xs='true' lg='4'>
              <Alert
                lg='2'
                xs='true'
                show={show}
                variant={correctGuess == true ? 'success' : 'danger'}
              >
                {parseInt(w) > parseInt(props.randomNum) && correctGuess != true
                  ? 'Too High!'
                  : 'Too Low!'}
              </Alert>
            </Col>
          </Row>
        ))}
      </ListGroup>
      <div className='text-center'>
        <Button
          size='lg'
          variant='outline-primary'
          className='mt-5'
          onClick={() => {
            window.location.reload()
          }}
          style={{ visibility: correctGuess ? 'visible' : 'hidden' }}
        >
          Play Again
        </Button>
      </div>
    </>
  )
}

function Number () {
  const [max, setMax] = useState('')
  const [isChecked, setChecked] = useState(false)

  const nums = [
    { value: 10 },
    { value: 25 },
    { value: 50 },
    { value: 100 },
    { value: 500 },
    { value: 1000 }
  ]

  const change = e => {
    setMax(parseInt(e.target.value))
    setChecked(true)
  }

  return (
    <Container>
      <h1>Pick a number:</h1>
      <Row className='justify-content-md-center'>
        <Col lg='5'>
          {nums.map((num, idx) => (
            <>
              <ToggleButton
                key={idx}
                id={`radio-${idx}`}
                className='m-1'
                type='radio'
                variant='outline-primary'
                name='radio'
                value={num.value}
                checked={max === parseInt(num.value)}
                onChange={e => change(e)}
                disabled={isChecked}
              >
                {num.value}
              </ToggleButton>
            </>
          ))}
        </Col>
      </Row>

      <GenerateRandomNum max={max} isChecked={isChecked} />
    </Container>
  )
}

const GenerateRandomNum = props => {
  const num = props.max
  var randomNum = Math.floor(Math.random() * (num - 1)) + 1
  if (randomNum % 5 == 0) {
    randomNum = Math.floor(Math.random() * (num - 1)) + 1
  }

  return (
    <>
      <LetUserPick
        randomNum={randomNum}
        isChecked={props.isChecked}
        max={num}
      />
    </>
  )
}

export default Number
