function Number () {
  const [max, setMax] = useState(0)
  const [checked, setChecked] = useState(false)

  const nums = [
    { name: '2000', value: 2000 },
    { name: '10001', value: 10001 }
  ]

  const change = e => {
    setMax(parseInt(e.currentTarget.value))
    console.log(generateRandomNum(max))
  }

  return (
    <>
      <h1>Pick a number:</h1>
      <Row className='justify-content-md-center'>
        {nums.map((num, idx) => (
          <>
            <Col xs='auto' key={idx}>
              <ToggleButton
                key={idx}
                className='m-2'
                id={`radio-${idx}`}
                type='radio'
                variant='outline-primary'
                name='radio'
                value={num.value}
                checked={max === parseInt(num.value)}
                onChange={e => change(e)}
              >
                {num.name}
              </ToggleButton>
            </Col>
          </>
        ))}
      </Row>
    </>
  )
}

function generateRandomNum (max) {
  var randomNum = Math.floor(Math.random() * (max - 1)) + 1
  while (randomNum == 0) {
    randomNum = Math.floor(Math.random() * (max - 1)) + 1
    break
  }
  return randomNum
}

export default Number
